import React, { useRef, useEffect } from 'react'
import { gsap }from 'gsap'

const Animation = () => {
    return (
        <div style={{
            width: '160px',
            height: '160px',
            background: 'salmon'
        }} />
    )
}

const Animation = () => {
    const boxRef = useRef()

    return (
        <div ref={boxRef} style={{
            width: '160px',
            height: '160px',
            background: 'salmon'
        }} />
    )
}

const Animation = () => {
    const boxRef = useRef()

    useEffect(() => {
        gsap.to([boxRef.current], {
            x: '400px',
            duration: 2
        })
    })

    return (
        <div ref={boxRef} style={{
            width: '160px',
            height: '160px',
            background: 'salmon'
        }} />
    )
}

