import { mdx } from '@mdx-js/react';
/* @jsxRuntime classic */
/* @jsx mdx */

export const _frontmatter = {
  "title": "Speaking the GraphQL Query Language",
  "slug": "graphql",
  "updated": "2019-07-30T00:00:00.000Z",
  "published": true,
  "type": ["note"],
  "topics": ["Web Development", "Illustrated Notes"],
  "growthStage": "Evergreen"
};
const makeShortcode = name => function MDXDefaultShortcode(props) {
      console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
      return <div {...props}/>
    };
const TwoCol = makeShortcode("TwoCol");
const Footnote = makeShortcode("Footnote");
const ImageFrame = makeShortcode("ImageFrame");
const BasicImage = makeShortcode("BasicImage");
const Center = makeShortcode("Center");
const layoutProps = {
  _frontmatter
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">
    <TwoCol GridColCount="3fr 4fr" gridGap="2em" mdxType="TwoCol">
      <p><img parentName="p" {...{
          "src": "https://res.cloudinary.com/dg3gyk0gu/image/upload/c_scale,w_300/v1592859008/maggieappleton.com/egghead-course-notes/graphql/thumb_2x.png",
          "alt": "Small GraphQL schema dictionary illustration"
        }}></img></p>
      <span>
        <p><a parentName="p" {...{
            "href": "https://graphql.org/"
          }}>{`GraphQL`}</a>{` is undoubtedly one of the popular kids on the Web Development playground right now.`}</p>
        <p>{`If you haven't heard of GraphQL, the TLDR is that it's a new way to request data from an API. `}<Footnote idName={1} mdxType="Footnote">{`If you're unsure what an API is, head on over to my illustrated article on `}<a parentName="p" {...{
              "href": "/api"
            }}>{`WTF is an API?`}</a></Footnote></p>
      </span>
    </TwoCol>
    <p>{`One that's easier, faster, better, and stronger than the old `}<a parentName="p" {...{
        "href": "https://en.wikipedia.org/wiki/Representational_state_transfer"
      }}>{`RESTful API system`}</a></p>
    <p>{`If you're lucky enough to already grok GraphQL, you'll know the `}<em parentName="p">{`QL`}</em>{` stands for `}<em parentName="p">{`Query Language`}</em>{` – aka. the language we talk to our GraphQL API in.  `}</p>
    <p>{`It's not a terribly complex language, but you'll still need to do some syntax studying to get the hang of it.`}</p>
    <p>{`Thankfully the magnificent `}<a parentName="p" {...{
        "href": "https://twitter.com/eveporcello"
      }}>{`Eve Porcello`}</a>{` made an `}<a parentName="p" {...{
        "href": "https://egghead.io/courses/graphql-query-language"
      }}>{`egghead course`}</a>{` that runs you through the full GraphQL vocabulary.`}</p>
    <a noTip href="https://egghead.io/courses/graphql-query-language"><ImageFrame width="80%" alt="Course illustration of a set of dictionary books with the word 'schema' across them" src="https://og-image-egghead-course.now.sh/graphql-query-language" mdxType="ImageFrame" /></a>
    <p>{`It's a wonderful introduction for anyone new to the GraphQL ecosystem – Eve explains all the parts of the playground interface that are not in the least bit intuitive. I'd been stumbling about in my own GraphQL queries lost and afraid until I worked through this.`}</p>
    <p>{`Anyways, as usual I drew some things:`}</p>
    <BasicImage alt="How to talk to a GraphQL API. We can use the graphQL playground to send queries to a GraphQL endpoint. The playground lets us explore our data before making any requests" src="https://res.cloudinary.com/dg3gyk0gu/image/upload/c_scale,w_700/v1592262603/maggieappleton.com/egghead-course-notes/graphql/graphql1.png" mdxType="BasicImage" />
    <BasicImage alt="A query is a request for data. We open it with the query keyword and a set of curly braces. Curly braces wrap around a selection and say go look inside this object. Blank spaces at the end of a line are fields that say I want his data please. We can also add the 'total' and 'all' keywords in front of any list of objects." src="https://res.cloudinary.com/dg3gyk0gu/image/upload/c_scale,w_900/v1592262605/maggieappleton.com/egghead-course-notes/graphql/graphql4.png" mdxType="BasicImage" />
    <BasicImage alt="Inside the playground, ctrl + space is a magic combination that reveals all the available fields of a query" src="https://res.cloudinary.com/dg3gyk0gu/image/upload/c_scale,w_900/v1592262603/maggieappleton.com/egghead-course-notes/graphql/graphql2.png" mdxType="BasicImage" />
    <BasicImage alt="We can filter our data by adding arguments to our queries. This means we only get the data we want. We can also use mutations to change the data. This includes creating, updating, and deleting data." src="https://res.cloudinary.com/dg3gyk0gu/image/upload/c_scale,w_900/v1592262604/maggieappleton.com/egghead-course-notes/graphql/graphql3.png" mdxType="BasicImage" />
    <BasicImage alt="The full GraphQL query language illustrated note" src="https://res.cloudinary.com/dg3gyk0gu/image/upload/v1592262601/maggieappleton.com/egghead-course-notes/graphql/graphql_sketchnotes--mini.png" mdxType="BasicImage" />
    <Center mdxType="Center">
      <h1>{`Bonus Poster`}</h1>
    </Center>
    <p>{`Twitter kept bugging me to make prints out of these illustrated notes, so this one is now purchaseable as a real life object on the `}<a parentName="p" {...{
        "href": "https://store.egghead.io/"
      }}>{`egghead store`}</a>{`.`}</p>
    <p>{` There's framed versions and unframed, depending on how fancy you feel. You can `}<a parentName="p" {...{
        "href": "https://store.egghead.io/product/poster-graphql-query-language"
      }}>{`buy one here`}</a>{` if you so desire.`}</p>
    <TwoCol gridGap="1em" mdxType="TwoCol">
      <p><img parentName="p" {...{
          "src": "https://store.egghead.io/static/ab87b8544078b8d5049501abf8b2eac7/e9055/thumb-graphql-query-language-by-eve-porcello_2x_dd5b8572-fca2-4c36-9e4e-d4bfc00a35fb.png",
          "alt": "a framed graphql illustrated print"
        }}></img></p>
      <p><img parentName="p" {...{
          "src": "https://store.egghead.io/static/91261ff8c8395dbc141635030e138734/fd7f1/perspective_2x_420b9d42-49db-41ac-8aee-1b8fa25c56eb.png",
          "alt": "a close-up image of one section of the print"
        }}></img></p>
    </TwoCol>
    <hr></hr>
    <h3>{`Want more illustrated notes on web development?`}</h3>
    <p>{`Take a look at `}<a parentName="p" {...{
        "href": "/es2019"
      }}>{`A Fresh Serving of JavaScript ES2019`}</a>{`, `}<a parentName="p" {...{
        "href": "/contentful-gatsby"
      }}>{`The JAMStack, Gatsby & Contentful`}</a>{` or `}<a parentName="p" {...{
        "href": "/websecurity"
      }}>{`Essential Web Security Tactics`}</a></p>

    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
